DROP TABLE numeric_data;
CREATE TABLE numeric_data (
	col_tinyint	TINYINT,
	col_smallint	SMALLINT,
	col_int		INT,
	col_bigint	BIGINT,
	col_bit		BIT,
	col_decimal	DECIMAL(38),
	col_numeric	NUMERIC(38),
	col_smallmoney	SMALLMONEY,
	col_money	MONEY,
	col_float	FLOAT(53),
	col_real	REAL
);
INSERT INTO numeric_data VALUES (null, null, null, null, null, null, null, null, null, null, null);
INSERT INTO numeric_data VALUES (0, -32768, -2147483648, -9223372036854775808, 0, -2147483648, -214748.3648, -214748.3648, -922337203685477.5808, -1.79E+308, -3.40E+38);
INSERT INTO numeric_data VALUES (0, 32767, 2147483647, 9223372036854775807, 0, 2147483647, 214748.3647, 214748.3647, 922337203685477.5807, 1.79E+308, 3.40E+38);

DROP TABLE datetime_data;
CREATE TABLE datetime_data (
	col_date		DATE,
	col_datetime2		DATETIME2,
	col_time		TIME,
	col_datetimeoffset	DATETIMEOFFSET,
	col_datetime		DATETIME,
	col_smalldatetime	SMALLDATETIME
);
INSERT INTO datetime_data VALUES (null, null, null, null, null, null);
INSERT INTO datetime_data VALUES ('1753-12-31', '1753-12-31 01:00:00', '12:35:29.1234567', '2007-05-08 12:35:29.1234567 +12:15', '2007-05-08 12:35:29.123', '2015-12-31 02:00:00');

DROP TABLE string_data;
CREATE TABLE string_data (
	col_char	CHAR(2500),
	col_nchar	NCHAR(2500),
	col_varchar	VARCHAR(2500),
	col_nvarchar	NVARCHAR(2500),
	col_text	TEXT,
	col_ntext	NTEXT
);
INSERT INTO string_data VALUES ('', '', '', '', '', '');
INSERT INTO string_data VALUES (null, null, null, null, null, null);
INSERT INTO string_data VALUES ('char', 'nchar', 'varchar', '', 'text', 'ntext');

DROP TABLE binary_data;
CREATE TABLE binary_data (
	col_binary	BINARY(30),
	col_varbinary	VARBINARY(MAX),
	col_image	IMAGE,
	col_guid	UNIQUEIDENTIFIER
);
INSERT INTO binary_data VALUES (null, null, null, null);
INSERT INTO binary_data VALUES (CONVERT(BINARY, '0xFFD8FFE000', 1), CONVERT(VARBINARY, '0xFFD8FFE000', 1), CONVERT(IMAGE, '0xFFD8FFE000', 1), NEWID());

CREATE VIEW data_view AS SELECT * FROM string_data, numeric_data, datetime_data;

